<style scoped>
  .stack-group {
    aspect-ratio: 1 / 1;
    width: 18rem;
    position: relative;
  }

  .stack-group svg {
    fill: none;
  }

  .stack {
    width: 100%;
    height: 100%;
  }

  .stack-group [class*="card-"] {
    --duration: 2.5s;
    position: absolute;
    animation: shuffle var(--duration) ease infinite;
    top: 48.7%;
    left: 24.8%;
  }

  .card-bike {
    animation-delay: calc(var(--duration) / 3 * 1) !important;
  }

  .card-car {
    animation-delay: calc(var(--duration) / 3 * 2) !important;
  }

  @keyframes shuffle {
    0% {
      /* init */
      transform: rotate(-8.14deg) translate(0%, 0%);
      z-index: -1;
    }
    25% {
      /* outer point */
      transform: rotate(-18deg) translate(0%, -120%);
      z-index: -1;
    }
    25.1% {
      /* switch z-index */
      transform: rotate(-18deg) translate(0%, -120%);
      z-index: 1;
    }
    50% {
      /* end */
      transform: rotate(4deg) translate(32%, 4%);
      z-index: 1;
    }
    100% {
      /* stay at end */
      transform: rotate(4deg) translate(32%, 4%);
      z-index: 0;
    }
  }
</style>